'use strict';
var chai = require('chai');
var ZSchema = require('z-schema');
var validator = new ZSchema({});
var request = require('request');

chai.should();

require('dotenv').load();

describe('/pets/{petId}', function() {
  describe('get', function() {
    it('should respond with 200 successful operation', function(done) {
      /*eslint-disable*/
      var schema = {
        "$ref": "#/definitions/Pet"
      };

      /*eslint-enable*/
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          api_key: process.env.API_KEY
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(200);

        validator.validate(body, schema).should.be.true;
        done();
      });
    });

    it('should respond with 200 successful operation', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          api_key: process.env.API_KEY
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(200);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with 400 Invalid ID supplied', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          api_key: process.env.API_KEY
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(400);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with 400 Invalid ID supplied', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          api_key: process.env.API_KEY
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(400);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with 404 Pet not found', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          api_key: process.env.API_KEY
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(404);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with 404 Pet not found', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          api_key: process.env.API_KEY
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(404);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

  });

  describe('post', function() {
    it('should respond with 405 Invalid input', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: 'Bearer ' + process.env.PETSTORE_AUTH
        },
        form: {
          name: 'DATA GOES HERE',
          status: 'DATA GOES HERE'
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(405);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with 405 Invalid input', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: 'Bearer ' + process.env.PETSTORE_AUTH
        },
        form: {
          name: 'DATA GOES HERE',
          status: 'DATA GOES HERE'
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(405);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

  });

  describe('delete', function() {
    it('should respond with 400 Invalid pet value', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Custom-Header': { 
            api_key: 'DATA GOES HERE'
          },
          Authorization: 'Bearer ' + process.env.PETSTORE_AUTH
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(400);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with 400 Invalid pet value', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/pets/' + properties.petId + '',
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Custom-Header': { 
            api_key: 'DATA GOES HERE'
          },
          Authorization: 'Bearer ' + process.env.PETSTORE_AUTH
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal(400);

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

  });

});
