'use strict';
var chai = require('chai');
var request = require('request');

chai.should();

describe('/users/createWithArray', function() {
  describe('post', function() {
    it('should respond with default successful operation', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/users/createWithArray',
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        json: {
          body: 'DATA GOES HERE'
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal('DEFAULT RESPONSE CODE HERE');

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with default successful operation', function(done) {
      request({
        url: 'http://petstore.swagger.wordnik.com/v2/users/createWithArray',
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        json: {
          body: 'DATA GOES HERE'
        }
      },
      function(error, res, body) {
        if (error) return done(error);

        res.statusCode.should.equal('DEFAULT RESPONSE CODE HERE');

        body.should.equal(null); // non-json response or no schema
        done();
      });
    });

  });

});
