'use strict';
var chai = require('chai');
var ZSchema = require('z-schema');
var validator = new ZSchema({});
var supertest = require('supertest');
var api = supertest('http://petstore.swagger.wordnik.com'); // supertest init;

chai.should();

describe('/users/login', function() {
  describe('get', function() {
    it('should respond with 200 successful operation', function(done) {
      /*eslint-disable*/
      var schema = {
        "type": "string"
      };

      /*eslint-enable*/
      api.get('/v2/users/login')
      .query({
username: 'DATA GOES HERE',password: 'DATA GOES HERE'
      })
      .set('Accept', 'application/json')
      .expect(200)
      .end(function(err, res) {
        if (err) return done(err);

        validator.validate(res.body, schema).should.be.true;
        done();
      });
    });

    it('should respond with 200 successful operation', function(done) {
      api.get('/v2/users/login')
      .query({
username: 'DATA GOES HERE',password: 'DATA GOES HERE'
      })
      .set('Accept', 'application/json')
      .expect(200)
      .end(function(err, res) {
        if (err) return done(err);

        res.body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with 400 Invalid username/password...', function(done) {
      api.get('/v2/users/login')
      .query({
username: 'DATA GOES HERE',password: 'DATA GOES HERE'
      })
      .set('Accept', 'application/json')
      .expect(400)
      .end(function(err, res) {
        if (err) return done(err);

        res.body.should.equal(null); // non-json response or no schema
        done();
      });
    });

    it('should respond with 400 Invalid username/password...', function(done) {
      api.get('/v2/users/login')
      .query({
username: 'DATA GOES HERE',password: 'DATA GOES HERE'
      })
      .set('Accept', 'application/json')
      .expect(400)
      .end(function(err, res) {
        if (err) return done(err);

        res.body.should.equal(null); // non-json response or no schema
        done();
      });
    });

  });

});
